import './App.css';
import header from './assets/panel grande.png';
import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

function App() {
  const [formValues, setFormValues] = useState({
    userName: "",
    password: ""
  });

  const [validationStates, setValidationStates] = useState({
    userNameState: true, // True when valid
    passwordState: true, // True when valid
  });

  const navigate = useNavigate(); // Hook para la redirección

  const validateUserName = (userName) => {
    return userName.length > 0; // Validar que el nombre de usuario no esté vacío
  };

  const validatePassword = (password) => {
    return password.length > 0;
  };

  const handleUserChange = (e) => {
    setFormValues({ ...formValues, userName: e.target.value });
  };

  const handlePasswordChange = (e) => {
    setFormValues({ ...formValues, password: e.target.value });
  };

  const clickSubmit = () => {
    const isValidUserName = validateUserName(formValues.userName);
    const isValidPassword = validatePassword(formValues.password);

    setValidationStates({
      userNameState: isValidUserName,
      passwordState: isValidPassword,
    });

    if (isValidUserName && isValidPassword) {
      // Redirigir a robots si las credenciales son válidas
      navigate("/robots");
    } else {
      alert("Please correct the errors before submitting.");
    }
  };

  return (
    <div>
      <img src={header} />
      <Form>
        <Form.Group className="mb-6" controlId="formBasicUserName">
          <Form.Label>Nombre de usuario</Form.Label>
          <Form.Control
            type="text"
            placeholder="Ingresa tu nombre de usuario"
            onChange={handleUserChange}
            value={formValues.userName}
            isInvalid={!validationStates.userNameState}
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Contraseña</Form.Label>
          <Form.Control
            type="password"
            placeholder="Contraseña"
            onChange={handlePasswordChange}
            value={formValues.password}
            isInvalid={!validationStates.passwordState}
          />
        </Form.Group>

        <Button variant="primary" onClick={clickSubmit}>
          Submit
        </Button>
      </Form>
    </div>
  );
}

export default App;
