import './App.css';
import header from './assets/panel grande.png';
import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";

function App() {
  const [formValues, setFormValues] = useState({
    userName: "",
    password: ""
  });

  const [validationStates, setValidationStates] = useState({
    userNameState: true, // True when valid
    passwordState: true, // True when valid
  });

  // Validación de nombre de usuario (puedes ajustar la lógica según tu criterio)
  const validateUserName = (userName) => {
    return userName.length > 0; // Simple validation: username must not be empty
  };

  // Validación de contraseña
  const validatePassword = (password) => {
    // Debe contener al menos 9 caracteres, incluyendo letras y números
    const passwordRegex = /^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{9,}$/;
    return passwordRegex.test(password);
  };

  const handleUserChange = (e) => {
    setFormValues({ ...formValues, userName: e.target.value });
  };

  const handlePasswordChange = (e) => {
    setFormValues({ ...formValues, password: e.target.value });
  };

  const clickSubmit = () => {
    // Validar nombre de usuario y contraseña al enviar
    const isValidUserName = validateUserName(formValues.userName);
    const isValidPassword = validatePassword(formValues.password);

    setValidationStates({
      userNameState: isValidUserName,
      passwordState: isValidPassword
    });

    if (isValidUserName && isValidPassword) {
      // Procesar el envío si las credenciales son válidas
      alert(JSON.stringify(formValues));
    } else {
      // Mostrar errores de validación
      alert("Please correct the errors before submitting.");
    }
  };

  return (
    <div>
      <Form>
        <Form.Group className="mb-6" controlId="formBasicUserName">
          <Form.Label>Nombre de usuario</Form.Label>
          <Form.Control
            type="text"
            placeholder="Ingresa tu nombre de usuario"
            onChange={handleUserChange}
            value={formValues.userName}
            isInvalid={!validationStates.userNameState}
          />
          { !validationStates.userNameState && (
            <Form.Text className="text-danger">
              El nombre de usuario no puede estar vacío.
            </Form.Text>
          )}
          { validationStates.userNameState && (
            <Form.Text className="text-muted">
              El nombre de usuario es obligatorio.
            </Form.Text>
          )}
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Contraseña</Form.Label>
          <Form.Control
            type="password"
            placeholder="Contraseña"
            onChange={handlePasswordChange}
            value={formValues.password}
            isInvalid={!validationStates.passwordState}
          />
          { !validationStates.passwordState && (
            <Form.Text className="text-danger">
              La contraseña debe tener al menos 9 caracteres, incluyendo letras y números.
            </Form.Text>
          )}
          { validationStates.passwordState && (
            <Form.Text className="text-muted">
              Asegúrate de que tu contraseña tenga números y letras y sea de al menos 9 caracteres.
            </Form.Text>
          )}
        </Form.Group>

        <Button variant="primary" onClick={clickSubmit}>
          Submit
        </Button>
      </Form>
    </div>
  );
}

export default App;
